import React from "react";
import { useEffect,useState } from "react";
import Sppiner from "./Sppiner";

const News=()=>{
    const [news, setNews] = useState([]);
    const [page,SetPage]=useState(1);
    const[pageSize,setPageSize]=useState(8);
    const[loader,setLoad]=useState(false);


  useEffect(() => {
    const fetchNews = async () => {
      try {
        const url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=82ecff82bdf1451bbbd48e6bf77ec5f6&page=${page}&pageSize=${pageSize}`;
        const response = await fetch(url);
        const result = await response.json();
        console.log(result.articles)
        setNews(result.articles);
         // Assuming "sources" is the correct property from the API response
      } catch (error) {
        console.error("Error fetching news:", error);
      }
    };

    fetchNews(); // Call the async function to fetch data
  }, []);
  const Next=async()=>{
    setLoad(true)
    const url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=82ecff82bdf1451bbbd48e6bf77ec5f6&page=${page}&pageSize=${pageSize}`;
    const response = await fetch(url);
    const result = await response.json();
    console.log(result.articles)
    setNews(result.articles);
    SetPage(page+1);
    setLoad(false)

  }
  const preview=async()=>{
    const url = `https://newsapi.org/v2/top-headlines?country=us&apiKey=82ecff82bdf1451bbbd48e6bf77ec5f6&page=${page}&pageSize=${pageSize}`;
    const response = await fetch(url);
    const result = await response.json();
    console.log(result.articles)
    setNews(result.articles);
    SetPage(page-1)

  }
    return(<>
    <Sppiner/>
    <div className="Container">
    <div className="News_container">
        {news.map((value) => (
          <div className="News_Cards" key={value.url}>
            <div className="Image">
              <img src={!value.urlToImage?"data:image/jpeg;base64,":value.urlToImage} alt={value.title} /> {/* Use alt attribute for accessibility */}
            </div>
            <div className="Heading">
              <h3>{value.source.name}</h3> {/* Use "source.name" instead of "sources.name" */}
            </div>
            <div className="Paragraph">
              <p>{value.title}</p> {/* Use "title" instead of "sources.title" */}
            </div>
            <div className="Link">
              <a href={value.url}><button type="button">CLICKS</button></a>
            </div>
          </div>
))}
        <div className="btn">
          <div className="btn1">
            <button onClick={preview} id="btn">Previous</button>
          </div>
          <div className="btn2">
            <button onClick={Next} id="btn">Next</button>
          </div>
        </div>
      </div> 
    </div>
    </>)
}
export default News
